import java.io.*;
import java.util.Scanner;
public class TestClass {
	 public static void main(String[] args) { 
		
       Scanner sc = new Scanner(System.in);
       int T = sc.nextInt();
       String abc = sc.nextLine();
       while(T > 0)
       {
       		String str = sc.nextLine();
         	encrypt(str);
       		T--;
       }
	}
  
  	public static void encrypt(String S)
    {
     
      	char orig[] = S.toCharArray();
      	String arr[] = new String[orig.length*2];
        int s = 0,j = 0,k = 0;
        char st = 'n';
      	for (int i = 0;i < orig.length;i++)
        {
          	s = 0;         	
            for (;j < orig.length;j++)
            {
             	if (orig[j] == orig[i])
                {
                  s = s + 1;
                  st = orig[j];
                }
              	else           
                	break;
              
            }
          	arr[k] = String.valueOf(st);
          	k++;
          	arr[k] = String.valueOf(s);
          	k++;
        }

      	for (int i = 0;i < arr.length; i++)
        {
          try
          {
         	if (Integer.parseInt(arr[i]) == 0)
            {
             	arr = delete_ele(arr,i,i-1);
              	
                i = -1;
            }
          }
          catch (Exception e) {}
        }
      	for (int i = arr.length-1;i >= 0;i--)
        	System.out.print(arr[i]);
      	System.out.println();
    }
  	public static String[] delete_ele(String arr[], int f, int s)
    {
     	String temp[] = new String[arr.length-2];
      	String ans[] = new String[temp.length];
        int j = 0;
      	for (int i = 0;i < arr.length;i++)
        {
         	if (i != f && i != s)
            {
              temp[j] = arr[i];
              j++;
            }
        }
      	for (int i = 0;i < temp.length;i++)
        {
          ans[i] = temp[i];
        }
      	return ans;
    }
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      